import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  useEffect(() => {
    axios.get('http://localhost:8000/api/tasks')
      .then(response => {
        setTasks(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const addTask = () => {
    if (newTask) {
      axios.post('http://localhost:8000/api/tasks', { task: newTask })
        .then(response => {
          setNewTask('');
          fetchTasks();
        })
        .catch(error => {
          console.error(error);
        });
    }
  };

  const deleteTask = (index) => {
    axios.delete(`http://localhost:8000/api/tasks/${index}`)
      .then(response => {
        fetchTasks();
      })
      .catch(error => {
        console.error(error);
      });
  };

  const fetchTasks = () => {
    axios.get('http://localhost:8000/api/tasks')
      .then(response => {
        setTasks(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <div>
      <h1>To-Do List</h1>
      <div>
        <input
          type="text"
          placeholder="Add a task"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <button onClick={addTask}>Add</button>
      </div>
      <ul>
        {tasks.map((task, index) => (
          <li key={index}>
            {task} <button onClick={() => deleteTask(index)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskList;
